import React, { useEffect, useState } from 'react';
import axios from "axios"
import "./MainPage.css"

function MainPage() {
    const [results, setResults] = useState([])
    const [gameCards, setGameCards] = useState([])

    const fetcher = async (e) => {
        e.preventDefault()
        await axios.get("http://localhost:8080/")
            .then(res => {
                res.data.forEach(obj => {
                    setResults(prev => [...prev, obj])
                })
                console.log(results)
            }).catch(e => {
                console.log(e)
            })
    }

    const fetchCards = async () => {
        await axios.get(`http://localhost:8080/games`)
            .then(res => {
                console.log(res.data)
                setGameCards(...gameCards, res.data)
            }).catch(e => {
                console.log(e)
            })
    }

    useEffect(() => {
        fetchCards()
    }, []);



    return (
        <div className="container">
            <h1>Games</h1>
            {
                gameCards ? gameCards.map(game =>{

                return(
                <div className="card" style={{ width: "5rem", height: "5rem" }}>
                    <img src={game.background_image} className="card-img-top" alt="Game image" />
                    <div className="card-body">
                        <h5 className="card-title">Card title</h5>
                        <p className="card-text">Some quick example text to build on the card title and make up the bulk of the card's content.</p>
                        <a href="#" className="btn btn-primary">Go somewhere</a>
                    </div>
                </div>
                )}) : null
            }

        </div>
    )
}

export default MainPage;
